import React from 'react';
import '../style/form.css';
import optionStates from '../states';

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      cpf: 0,
      adress: '',
      city: '',
    };
    this.htmlForm = this.htmlForm.bind(this);
  }

  renderHalf() {
    return (
      <>
        <div>
          <label htmlFor='State'>State</label>
          <select>
            {optionStates.map((state) => (
              <option value={state}>{state}</option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor='Type'>Home Type</label>
          <label for='typeApto'>Apartamento</label>
          <input name='typeApto' type='radio' maxLength='40' required />
          <label for='typeHouse'>House</label>
          <input name='typeHouse' type='radio' maxLength='40' required />
        </div>
      </>
    );
  }

  render() {
    return (
      <form>
        <fieldset>
          <div>
            <label htmlFor='Name'>Name</label>
            <input value={this.state.name} name='Name' type='text' maxLength='40' required />
          </div>
          <div>
            <label htmlFor='Email'>Email</label>
            <input
              value={this.state.email}
              name='Email'
              type='text'
              maxLength='50'
              required
              pattern='[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$'
            />
          </div>
          <div>
            <label htmlFor='CPF'>CPF</label>
            <input value={this.state.cpf} name='CPF' type='text' maxLength='11' required />
          </div>
          <div>
            <label htmlFor='Adress'>Adress</label>
            <input value={this.state.adress} name='Adress' type='text' maxLength='200' required />
          </div>
          <div>
            <label htmlFor='City'>City</label>
            <input value={this.state.city} name='City' type='text' maxLength='28' required />
          </div>
          {this.renderHalf()}
        </fieldset>
      </form>
    );
  }
}

export default Form;
