import React from 'react';
import '../style/form.css';
import optionStates from '../states';

const initialState = {
  name: '',
  email: '',
  cpf: 0,
  adress: '',
  city: '',
};

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      initialState,
    };
    this.renderHalf = this.renderHalf.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  renderHalf() {
    return (
      <>
        <div>
          <label htmlFor='City'>City</label>
          <input
            value={this.state.city}
            onChange={this.handleChange()}
            name='City'
            type='text'
            maxLength='28'
            required
          />
        </div>
        <div>
          <label htmlFor='State'>State</label>
          <select>
            {optionStates.map((state) => (
              <option value={state}>{state}</option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor='Type'>Home Type</label>
          <input name='typeApto' type='radio' maxLength='40' required />
          <label for='typeApto'>Apartamento</label>
          <input name='typeHouse' type='radio' maxLength='40' required />
          <label for='typeHouse'>House</label>
        </div>
      </>
    );
  }

  render() {
    return (
      <form>
        <fieldset>
          <div>
            <label htmlFor='Name'>Name</label>
            <input
              value={this.state.name}
              onChange={this.handleChange()}
              name='Name'
              type='text'
              maxLength='40'
              required
            />
          </div>
          <div>
            <label htmlFor='Email'>Email</label>
            <input
              value={this.state.email}
              onChange={this.handleChange()}
              name='Email'
              type='text'
              maxLength='50'
              required
              pattern='[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$'
            />
          </div>
          <div>
            <label htmlFor='CPF'>CPF</label>
            <input
              value={this.state.cpf}
              onChange={this.handleChange()}
              name='CPF'
              type='text'
              maxLength='11'
              required
            />
          </div>
          <div>
            <label htmlFor='Adress'>Adress</label>
            <input
              value={this.state.adress}
              onChange={this.handleChange()}
              name='Adress'
              type='text'
              maxLength='200'
              required
            />
          </div>
          {this.renderHalf()}
        </fieldset>
      </form>
    );
  }
}

export default Form;
